mapper.update:
SELECT goods_total_num FROM sw_goods
WHERE  sw_goods.tran_no=#{tranNo,jdbcType=VARCHAR}
  FOR UPDATE

mapper.select:
SELECT
b.goods_code, b.goods_name, b.goods_company, b.create_time, b.goods_total_num, b.sw_store_id, b.sw_store_name, a.is_enabled FROM sw_store a INNER JOIN sw_goods b on a.id=b.sw_store_id
b.is_enabled=#{isEnabled}
AND b.create_time >= #{startTime}
AND b.create_time <=#{endTime}
and b.goods_name=#{goodsName}
and b.goods_code=#{goodsCode}
Order By b.create_time Desc
